<?phpif(isset($_SESSION['message']) && trim($_SESSION['message']) !== ""){?><div class="alert alert-success">	<i class="icon-ok-sign"></i><?php echo $_SESSION['message'];?></div><?phpunset($_SESSION['message']);}elseif(isset($_SESSION['errmsg']) && trim($_SESSION['errmsg']) !== ""){?><div class="alert alert-error">	<i class="icon-remove-sign"></i><?php echo $_SESSION['errmsg'];?></div><?phpunset($_SESSION['errmsg']);}?>