;        }    });});$(document).ready(function() {    $("#date_of_birth").birthdayPicker({        "defaultDate": "<?php echo $date_of_birth!=='0000-00-00' ? date('Y-m-d', strtotime($date_of_birth)) : '';?>",        "maxYear": "<?php echo date('Y');?>",        "maxAge": 100,        "minAge": 1,        "placeholder": true,        //"defaultDate": true,        "sizeClass": "span2"    });    $("#state_id").val("<?php echo $state_id; ?>").trigger('change');    setTimeout(function() {        $("#city_id").val("<?php echo $city_id; ?>");    }, 1500);    jQuery.validator.addMethod("validateSSN", function(value, element, params) {        var pattern = /^\d{3}-\d{2}-\d{4}$/;        return ssn.match(value);    },'Social security number is not valid');    // USer login validation & submission    var v = $("#frmSignup").validate({        rules: {            firstname: {                required: true,                minLength:2            },            lastname: {                required: true,                minLength:2            },            social_security_number: {                required: true,                validateSSN:true            },            email: {                required: true,                email: true            },            password: {                required: true,                minlength: 6            },            confirm_password: {                required: true,                equalTo: "#password"            },            phone: "required",            address: "required",            state_id: {                required: true,                digits: true            },            city_id: {                required: true,                digits: true            },            zipcode: "required",            employer: "required",            reason: "required",        },        errorElement: "span",        errorClass: "help-inline-error"    });});$(document).on("change", "#state_id", function (e) {    var state_id = $(this).val();    if(state_id=='' || state_id==null) {        $("#city_id option").remove();        return false;    }    $(this).after('<img class="loading" src="<?php echo IMG_PATH;?>loading.gif" />');    $.ajax({        url:'<?php echo AJAX_PATH;?>listCityByState.php?state_id='+state_id,        cache: false,        dataType:"html",        success:function(data){            $("#city_id option").remove();            $("#city_id").append(data);            $(".loading").remove();        }    });});</script><?php include(LIB_HTML . 'footer.php');?>